import React, { useState } from 'react';
import { useInView } from 'react-intersection-observer';
import './Body.css';  // Assuming you will create a Header.css file for styling

function Body() {

    const { ref: aboutRef, inView: aboutInView } = useInView({
        triggerOnce: false,
        threshold: 0.25  // Trigger when 50% of the element is in view
    });

    const { ref: experienceRef, inView: experienceInView } = useInView({
        triggerOnce: false,
        threshold: 0.25
    });

    const { ref: resumeRef, inView: resumeInView } = useInView({
        triggerOnce: false,
        threshold: 0.25
    });

    const { ref: contactRef, inView: contactInView } = useInView({
        triggerOnce: false,
        threshold: 0.25
    });



    const [selectedProject, setSelectedProject] = useState(null);

    const projects = [
        { id: 1, title: "Project 1", desc: "Description for Project 1", img: "/path-to-image1.png" },
        { id: 2, title: "Project 2", desc: "Description for Project 2", img: "/path-to-image2.png" },
        { id: 3, title: "Project 3", desc: "Description for Project 3", img: "/path-to-image3.png" }
    ];

    const { ref, inView } = useInView({
        triggerOnce: true,
        threshold: 0.5
    });

    const handleProjectClick = (project) => {
        setSelectedProject(project);
    };



    return (
        <div>        
            <div class = "about">
                <div class="intro-tab">
                    <p className="intro-text"><strong>Welcome!</strong> ðŸ‘‹ You're in the right place.</p>
                </div>

                <div class="intro-container">

                    <div class="profile-img-container">
                        <img class="profile" src="/grad_pic.png" alt="Gyury Lee" />
                    </div>

                    <div class="main-intro-container">
                        <p class="main-intro">
                            Nice to meet you! I am a junior currently enrolled as an Info-Science major
                            and a CS minor at the University of Illinois Urbana-Champaign. My relevant
                            coursework includes topics such as Machine Learning, Data Structures, and
                            Linear Algebra, and I participate in CTF events in my extracurricular group
                            SigPwny here at the university.
                        </p>
                    </div>
                </div>
            </div>

            <div class = "contents"> 



                <div className="experience" ref={ref} style={{ opacity: inView ? 1 : 0 }}>
                <h2>Experience</h2>
                <div className="projects">
                    {projects.map(project => (
                        <div key={project.id} className="project" onClick={() => handleProjectClick(project)}>
                            <img src={project.img} alt={project.title} />
                            <div className="overlay">
                                <div className="project-title">{project.title}</div>
                            </div>
                        </div>
                    ))}
                </div>
                {selectedProject && (
                    <div className="popup">
                        <div className="popup-inner">
                            <img src={selectedProject.img} alt={selectedProject.title} />
                            <p>{selectedProject.desc}</p>
                        </div>
                        <button onClick={() => setSelectedProject(null)}>Close</button>
                    </div>
                )}
            </div>





            </div>
        </div>

    );
}

export default Body;
