import React, { useState } from 'react';
import { useInView } from 'react-intersection-observer';
import './Body.css';




function Body() {
    const [selectedProject, setSelectedProject] = useState(null);

    const projects = [
        { id: 1, title: "Project 1", desc: "Description for Project 1", img: "/path-to-image1.png" },
        { id: 2, title: "Elden Ring Speedrum Optimizer", desc: "As a student in a team of software developers, optimized speedrunning routes in the video game Elden Ring by representing the game's map as a weighted, directed graph with bosses as nodes and travel times as edges. We implemented various algorithms to find the shortest paths for both comprehensive (100% completion) and specific boss-to-boss routes, and displayed the computed paths using a Python GUI. Data for the graph was meticulously gathered from actual gameplay by one of our team members, an avid Elden Ring enthusiast.", img: "/ER_project.png" },
        { id: 3, title: "Project 3", desc: "Description for Project 3", img: "/path-to-image3.png" }
    ];

    const { ref: aboutRef, inView: aboutInView } = useInView({
        triggerOnce: false,
        threshold: 0.25
    });

    const { ref: experienceRef, inView: experienceInView } = useInView({
        triggerOnce: false,
        threshold: 0.25
    });

    const { ref: resumeRef, inView: resumeInView } = useInView({
        triggerOnce: false,
        threshold: 0.25
    });

    const { ref: contactRef, inView: contactInView } = useInView({
        triggerOnce: false,
        threshold: 0.25
    });

    const popup_overlay = document.querySelector('.popup_overlay');

    project.forEach(project => {
        project.addEventListener('click', () => {
            popup_overlay.style.display = 'block';
            setTimeout(() => popup_overlay.style.opacity = 1, 10); // Delay to ensure it's rendered
        });
    });

    // Assuming you have a close button inside your popup
    document.querySelector('.exit_button').addEventListener('click', () => {
        popup_overlay.style.opacity = 0;
        setTimeout(() => popup_overlay.style.display = 'none', 500); // Wait for opacity transition
    });

    return (
        <div className="body">        
            <div className="about" ref={aboutRef} style={{ opacity: aboutInView ? 1 : 0 }}>
                <div className="intro-tab">
                    <p className="intro-text"><strong>Welcome!</strong> ðŸ‘‹ You're in the right place.</p>
                </div>

                <div className="intro-container">
                    <div className="profile-img-container">
                        <img className="profile" src="/grad_pic.png" alt="Gyury Lee" />
                    </div>

                    <div className="profile-img-container">
                        <p className="main-intro">
                            Nice to meet you! I am a junior currently enrolled as an Info-Science major
                            and a CS minor at the University of Illinois Urbana-Champaign. My relevant
                            coursework includes topics such as Machine Learning, Data Structures, and
                            Linear Algebra, and I participate in CTF events in my extracurricular group
                            SigPwny here at the university.
                        </p>
                    </div>
                </div>
            </div>

            <div className="contents"> 
                <div id="experience" className={`section ${experienceInView ? 'section-visible' : ''}`} ref={experienceRef}>
                    <div><p className="exp_header"><strong>Experience</strong></p></div>
                    <div className="projects">
                        {projects.map(project => (
                            <div key={project.id} className="project" onClick={() => setSelectedProject(project)}>
                                <div className="project-image-container">
                                    <img src={project.img} alt={project.title} />
                                </div>
                                <div className="overlay">
                                    <div className="project-title">{project.title}</div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

                <div class="popup_overlay"></div>
                {selectedProject && (
                    <div className="popup">
                        <div className="popup_top">
                            <img src={selectedProject.img} alt={selectedProject.title} />
                        </div>
                        <div className="popup_bottom">
                            <p>{selectedProject.desc}</p>
                        </div>
                        <button className="exit_button" onClick={() => setSelectedProject(null)}>
                            <img src="/exit_button.png" alt="Close" />
                        </button>
                    </div>
                )}

                <div id="resume" className={`section ${resumeInView ? 'section-visible' : ''}`} ref={resumeRef}>
                    <h2>Resume</h2>
                    <p>Link or content of your resume...</p>
                </div>

                <div id="contact" className={`section ${contactInView ? 'section-visible' : ''}`} ref={contactRef}>
                    <h2>Contact</h2>
                    <p>How to contact you...</p>
                </div>        
            </div>
        </div>
    );
}

export default Body;
